import React, { Component } from 'react';

class Counter extends Component {
    state = { 
        count: 0
    };

    handleIncerement = () =>{
        this.setState({count: this.state.count + 1})
    }

    handleDecrement = () =>{
        this.setState(count >= 0 ? {count: this.state.count - 1} : {count : 0})
    }

    render() {
        return (
            <div>
                <span className = {this.getBadgeClasses()}>{this.formatCount()}</span>
                <button onClick = {this.handleIncerement} className = "btn btn-secondary btn-sm m-3">+</button>
                <button onClick = {this.handleDecrement} className = "btn btn-secondary btn-sm">-</button>
                {/* <ul>{this.state.tags.map(tag => <li key = {tag}>{tag}</li>)}</ul> */}
            </div>
        );
    }
    getBadgeClasses() {
        let classes = "badge m-3 badge-";
        // classes += (this.state.count === 0) ? "warning" : "primary";
        if(this.state.count === 0){
            classes += " ing";
        }
        if(this.state.count >= 0){
            classes += "primary";
        }
        if(this.state.count <= 0){
            classes += "danger";
        }
        return classes;
    }
    formatCount(){
        const {count} = this.state;
        return count === 0 ? 'Zero' : count;
    }
}

export default Counter;
