import React, { Component } from 'react';

class Counter extends Component {
    state = { 
        // value: this.props.value
        value: 0
    };

    handleIncerement = () =>{
        this.setState({value: this.state.value + 1})
    }

    handleDecrement = () =>{
        this.setState({value: this.state.value - 1})
    }

    render() {
        return (
            <div>
                {/* {this.props.children}           used when we dynamically pass any string*/}
                {/* <h4>{this.props.id}</h4>        used when we pass id */}
                <span className = {this.getBadgeClasses()}>{this.formatvalue()}</span>
                <button onClick = {this.handleIncerement} className = "btn btn-secondary btn-sm m-3">+</button>
                <button onClick = {this.handleDecrement} className = "btn btn-secondary btn-sm">-</button>
                {/* <ul>{this.state.tags.map(tag => <li key = {tag}>{tag}</li>)}</ul> */}
            </div>
        );
    }
    getBadgeClasses() {
        let classes = "badge m-3 badge-";
        // classes += (this.state.value === 0) ? "warning" : "primary";
        if(this.state.value === 0){
            classes += " ing";
        }
        if(this.state.value >= 0){
            classes += "primary";
        }
        if(this.state.value <= 0){
            classes += "danger";
        }
        return classes;
    }
    formatvalue(){
        const {value} = this.state;
        return value === 0 ? 'Zero' : value;
    }
}

export default Counter;
